memberdef.initializer:
do                                             \
{                                              \
	esp_err_t rc = (x);                        \
	if (rc != ESP_OK)                          \
	{                                          \
		ESP_LOGE("err", "esp_err_t = %d", rc); \
		assert(0 && #x);                       \
	}                                          \
} while (0);
